var mysql = require('mysql');

var con = mysql.createConnection({
  host: "localhost",
  user: "root",
  password: "senior1234",
  database: "senior_web"
});

con.connect(function(err) {
  if (err) throw err;
  console.log("Connected!");
});

module.export = connection;
