import bus from "../images/smBus.png"
import React from 'react'
import {Link} from "react-router-dom";

class SummaryTable extends React.Component{ 

   constructor(props) {
        super(props);
        this.state = {bus_id: null, amount_user: null, date: null, dataArr: []};
      }

    async componentDidMount() {
        const res = await fetch('http://127.0.0.1:5000/api/database/user_info');
        const data = await res.json();
        // const map1 = data.map(x =>
        //     console.log(x.bus_id, x.amount_user, x.date_time)
        // );
        this.setState({dataArr: data})
        // console.log(this.state.dataArr);
    }

   
    
   render(){
   return (
    <main className="main-content position-relative border-radius-lg ">
    <div class="container-fluid py-4 ">
      <div class="row">
        <div class="col-12">
          <div class="card mb-4">
            <div class="d-flex card-header pb-0">
              <h6>Bus table</h6>
            </div>
            <div class="card-body px-0 pt-0 pb-2">
              <div class="table-responsive p-0">
              <div className="App-overtable">
                <table class="table align-items-center mb-0 ">
                  <thead>
                    <tr>
                      <th class="text-xs font-weight-bold mb-0">Bus ID</th>
                      <th class="text-xs font-weight-bold mb-0">Status</th>
                      <th class="text-xs font-weight-bold mb-0">Number of Passenger</th>
                      <th class="text-xs font-weight-bold mb-0">Schedule</th>
                      <th class="text-xs font-weight-bold mb-0">Location</th>
                      
                    </tr>
                  </thead>
                  <tbody>
                    
                    {this.state.dataArr.map(value => {
                        return (
                          <React.Fragment>
                            <tr>
                                <td> 
                                  <div class=" px-4 py-1">
                                    <Link to="/Dashboard">
                                      <div>
                                        <img src={bus} class="navbar-brand-img h-100" alt="user1"/>
                                      </div>
                                      
                                      <div class="d-flex flex-column justify-content-center">
                                        <h6 class="mb-0 text-sm">{value.bus_id}</h6>
                                        {/* <p class="text-xs text-secondary mb-0">john@creative-tim.com</p> */}
                                      </div>
                                    </Link>
                                  </div>
                                </td>
                                <td class="align-middle text-center text-sm">
                                  <span class="badge badge-sm bg-gradient-success">Online</span>
                                </td>
                                <td>
                                  <p class="text-xs font-weight-bold mb-0">{value.amount_user}</p>
                                  {/* <p class="text-xs text-secondary mb-0">Organization</p> */}
                                </td>
                                
                                <td class="align-middle text-center">
                                  <span class="text-secondary text-xs font-weight-bold">{value.date_time}</span>
                                </td>
                                <td class="align-middle text-center">
                                  <span class="text-secondary text-xs font-weight-bold">Unknown</span>
                                </td>
                          </tr>
                        </React.Fragment>
                            )
                    })}
                         
                  </tbody>
                </table>
                </div> 
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </main>   
   )
   }
}

export default SummaryTable
