      }

    async componentDidMount() {
        if (this.state.dataArr.length !== 0){
            const res = await fetch('http://127.0.0.1:5000/api/database/user_info');
            const data = await res.json();
            const map1 = data.map(x =>
                console.log(x.bus_id, x.amount_user, x.date_time)
            );
            this.setState({dataArr: data})
            console.log(this.state.dataArr);
        }
  
    }

    render(){
        const { bus_id } = this.state;
        return (
            <div className='App'>
                Hello
                <br></br>
                {bus_id}
            </div>
        );
    }
}
export default TestFetch;
