const config = require('./database');
const sql = require('mysql');

const getBusData = async () =>{
    try{
        let pool = await sql.connect(config);
        let bus = await pool.request().query("SELECT * FROM bus_info");
        console.log(bus);
        return bus;
    }
    catch(error){
        console.log(error);
    }
}

module.export = getBusData;
