import React from 'react'
import "../pages/dashboard.css" 

class TestFetch extends React.Component{ 

    constructor(props) {
        super(props);
        this.state = {bus_id: null, amount_user: null, date: null, dataArr: []};
      }

    async componentDidMount() {
        const res = await fetch('http://127.0.0.1:5000/api/database/bus_info');
        const data = await res.json();
        // const map1 = data.map(x =>
        //     console.log(x.bus_id, x.amount_user, x.date_time)
        // );
        this.setState({dataArr: data})
        // console.log(this.state.dataArr);
    }

    render(){
        // console.log(this.state.dataArr);
        return (
            <div className='App'>
               
                    {this.state.dataArr.map(value => {
                        return (
                            <li key={value.bus_id}>
                                {value.bus_id}
                                <br/>
                                {value.amount_user}
                                <br/>
                                {value.date_time}
                                <br/>
                                <br/>
                                <br/>
                            </li>
                        )
                    })}
                
            </div>
            
        );
    }
}
export default TestFetch;
