import '../App.css'
import React from 'react';
// import location from "../images/location.png"

import moment from "moment";
import Chart from 'chart.js/auto';
import {Line} from 'react-chartjs-2';

class contentPage extends React.Component{ 

  constructor(props) {
    super(props);
    this.state = { dataArr: []};
  }
  

  async componentDidMount() {
      
      try {
        // https://magellan.ais.co.th/asgardpullmessagesapis/api/listen/thing?Key=8367724AE996DC0A35D386C801911DFB
        let res = await fetch(`http://127.0.0.1:5000/api/database/bus_info/summaryTable/${this.props.dataFromParent}`);
        let data = await res.json();
        this.setState({dataArr: data})
      }
      catch(error){
        console.log(error)
      }
      

  }

  render(){

    var time_location1 = [];
    var now_user_location1 = [];
    var time_location2 = [];
    var now_user_location2 = [];
    var time_location3 = [];
    var now_user_location3 = [];
    var time_location4 = [];
    var now_user_location4 = [];
    var time_location5 = [];
    var now_user_location5 = [];
    var time_location6 = [];
    var now_user_location6 = [];
    var time_location7 = [];
    var now_user_location7 = [];
    var time_location8 = [];
    var now_user_location8 = [];

    this.state.dataArr.map(value => {
      const time = moment(value.schedule_date).utc().local().format('h:mm a');
      if (value.location === 'station 1'){
        time_location1.push(time)
        now_user_location1.push(value.now_user)
      }
      else if (value.location === 'station 2'){
        time_location2.push(time)
        now_user_location2.push(value.now_user)
      }
      else if (value.location === 'station 3'){
        time_location3.push(time)
        now_user_location3.push(value.now_user)
      }
      else if (value.location === 'station 4'){
            time_location4.push(time)
            now_user_location4.push(value.now_user)
      }
      else if (value.location === 'station 5'){
        time_location5.push(time)
        now_user_location5.push(value.now_user)
      }
      else if (value.location === 'station 6'){
        time_location6.push(time)
        now_user_location6.push(value.now_user)
      }
      else if (value.location === 'station 7'){
        time_location7.push(time)
        now_user_location7.push(value.now_user)
      }
      else if (value.location === 'station 8'){
        time_location8.push(time)
        now_user_location8.push(value.now_user)
      }
    });

        return (
            <main class="main-content position-relative border-radius-lg ">
            <div className='App-overtable' id="style-1">
            <div class="container-fluid py-4">
              <div class="row">
                <div class="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                  <div class="card">
                    <div class="card-body p-3">
                      <div class="row">
                        <div class="col-8">
                          <div class="numbers">
                            <p class="text-sm mb-0 text-uppercase font-weight-bold">Bus Number</p>
                            <h5 class="font-weight-bolder">
                              888
                            </h5>
                            {/* <p class="mb-0">
                              <span class="text-success text-sm font-weight-bolder">+543 </span>
                              since last month
                            </p> */}
                          </div>
                        </div>
                        <div class="col-4 text-end">
                          <div class="icon icon-shape bg-gradient-primary shadow-primary text-center rounded-circle">
                            <i class="ni ni-single-02 text-lg opacity-10" aria-hidden="true"></i>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <div class="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                  <div class="card">
                    <div class="card-body p-3">
                      <div class="row">
                        <div class="col-8">
                          <div class="numbers">
                            <p class="text-sm mb-0 text-uppercase font-weight-bold">Now Passenger</p>
                            <h5 class="font-weight-bolder">
                              32
                            </h5>
                            {/* <p class="mb-0">
                              <span class="text-success text-sm font-weight-bolder">+18 </span>
                              since last 5 minutes
                            </p> */}
                          </div>
                        </div>
                        <div class="col-4 text-end">
                          <div class="icon icon-shape bg-gradient-success shadow-success text-center rounded-circle">
                            <i class="ni ni-badge text-lg opacity-10" aria-hidden="true"></i>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <div class="col-xl-3 col-sm-6">
                  <div class="card">
                    <div class="card-body p-3">
                      <div class="row">
                        <div class="col-8">
                          <div class="numbers">
                            <p class="text-sm mb-0 text-uppercase font-weight-bold">Current Station</p>
                            <h5 class="font-weight-bolder">
                              Siit
                            </h5>
                            <p class="mb-0">
                              {/* Next: <span class="text-success text-sm font-weight-bolder">TU dome</span>  */}
                            </p>
                          </div>
                        </div>
                        <div class="col-4 text-end">
                          <div class="icon icon-shape bg-gradient-warning shadow-warning text-center rounded-circle">
                            <i class="ni ni-pin-3 text-lg opacity-10" aria-hidden="true"></i>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <div class="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                  <div class="card">
                    <div class="card-body p-3">
                      <div class="row">
                        <div class="col-8">
                          <div class="numbers">
                            <p class="text-sm mb-0 text-uppercase font-weight-bold">Next Station</p>
                            <h5 class="font-weight-bolder">
                              TU dome
                            </h5>
                            {/* <p class="mb-0">
                              <span class="text-success text-sm font-weight-bolder">+18 </span>
                              since last hour
                            </p> */}
                          </div>
                        </div>
                        <div class="col-4 text-end">
                          <div class="icon icon-shape bg-gradient-danger shadow-danger text-center rounded-circle">
                            <i class="ni ni-circle-08 text-lg opacity-10" aria-hidden="true"></i>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>


              <div class="App-bg-graph">  
                <div class="row mt-4">
                {this.state.dataArr.map(value => {
                      var time_location = [];
                      var now_user_location = [];
                      if (value.location === 'station 1'){
                        time_location = time_location1;
                        now_user_location = now_user_location1;
                      }
                      else if (value.location === 'station 2'){
                        time_location = time_location2;
                      }
                      else if (value.location === 'station 3'){
                        time_location = time_location3;
                      }
                      else if (value.location === 'station 4'){
                        time_location = time_location4;
                      }
                      else if (value.location === 'station 5'){
                        time_location = time_location5;
                      }
                      else if (value.location === 'station 6'){
                        time_location = time_location6;
                      }
                      else if (value.location === 'station 7'){
                        time_location = time_location7;
                      }
                      else if (value.location === 'station 8'){
                        time_location = time_location8;
                      }

                  const data = {
                    labels: time_location,
                    datasets: [
                      {
                        label: "Current Passenger",
                        data: now_user_location3,
                        fill: true,
                        backgroundColor: "rgba(75,192,192,0.2)",
                        borderColor: "rgba(75,192,192,1)"
                      },
                      // {
                      //   label: "Incoming Passenger",
                      //   data: [value.get_in_user],
                      //   fill: false,
                      //   borderColor: "#742774"
                      // },
                      // {
                      //   label: "Outgoing Passenger",
                      //   data: [value.get_out_user],
                      //   fill: false,
                      //   borderColor: "#EF3A4C"
                      // }
                    ]
                  };

                  return (
                    <React.Fragment>
                          <div class="col-xl-6 col-sm-6">
                              {value.location}
                              {/* <canvas id="chart-line" class="chart-canvas" height="300"></canvas> */}
                              <Line data={data} />  
                          </div>
                    </React.Fragment>
                  )
                })}      
                </div>     
              </div>


              <div class="row mt-4">
                <div class="col-lg-12 mb-lg-0 mb-4">
                  <div class="card ">
                    <div class="card-header pb-0 p-3">
                      <div class="d-flex justify-content-between">
                        <h6 class="mb-2">History </h6>
                      </div>
                    </div>
                    <div class="table-responsive">
                      <table class="table align-items-center ">
                        <tbody>
                          <tr>
                            <td>
                              <div class="align-items-center">
                                {/* <div>
                                  <img src={location} alt="Country flag"/>
                                </div> */}
                                <div class="text-center">
                                  <p class="text-xs font-weight-bold mb-0">Date</p>
                                  {/* <h6 class="text-sm mb-0">25/8/2022</h6> */}
                                </div>
                              </div>
                            </td>
                            <td>
                              <div class="text-center">
                                <p class="text-xs font-weight-bold mb-0">Current Passenger</p>
                                {/* <h6 class="text-sm mb-0">6</h6> */}
                              </div>
                            </td>
                            <td>
                              <div class="text-center">
                                <p class="text-xs font-weight-bold mb-0">Getting in Passenger</p>
                                {/* <h6 class="text-sm mb-0">3</h6> */}
                              </div>
                            </td>
                            <td>
                              <div class="text-center">
                                <p class="text-xs font-weight-bold mb-0">Getting out Passenger</p>
                                {/* <h6 class="text-sm mb-0">0</h6> */}
                              </div>
                            </td>
                            <td class="align-middle text-sm">
                              <div class="col text-center">
                                <p class="text-xs font-weight-bold mb-0">Station</p>
                                {/* <h6 class="text-sm mb-0">Station 1</h6> */}
                              </div>
                            </td>
                            <td>
                              <div class="text-center">
                                <p class="text-xs font-weight-bold mb-0">Time</p>
                                {/* <h6 class="text-sm mb-0">10:59</h6> */}
                              </div>
                            </td>
                          </tr>
                          {this.state.dataArr.map(value => {
              
                              const day = moment(value.schedule_date).utc().local().format('DD/MM/YYYY');
                              const time = moment(value.schedule_date).utc().local().format('h:mm a');

                            return (
                              <React.Fragment>
                                <tr>
                                  <td>
                                    <div class="align-items-center">
                                      {/* <div>
                                        <img src={location} alt="Country flag"/>
                                      </div> */}
                                      <div class="text-center">
                                        {/* <p class="text-xs font-weight-bold mb-0">Date</p> */}
                                        <h6 class="text-sm mb-0">{day}</h6>
                                      </div>
                                    </div>
                                  </td>
                                  <td>
                                    <div class="text-center">
                                      {/* <p class="text-xs font-weight-bold mb-0">Current Passenger</p> */}
                                      <h6 class="text-sm mb-0">{value.now_user}</h6>
                                    </div>
                                  </td>
                                  <td>
                                    <div class="text-center">
                                      {/* <p class="text-xs font-weight-bold mb-0">Incoming Passenger</p> */}
                                      <h6 class="text-sm mb-0">{value.get_in_user}</h6>
                                    </div>
                                  </td>
                                  <td>
                                    <div class="text-center">
                                      {/* <p class="text-xs font-weight-bold mb-0">Outgoing Passenger</p> */}
                                      <h6 class="text-sm mb-0">{value.get_out_user}</h6>
                                    </div>
                                  </td>
                                  <td class="align-middle text-sm">
                                    <div class="col text-center">
                                      {/* <p class="text-xs font-weight-bold mb-0">Station</p> */}
                                      <h6 class="text-sm mb-0">{value.location}</h6>
                                    </div>
                                  </td>
                                  <td>
                                    <div class="text-center">
                                      {/* <p class="text-xs font-weight-bold mb-0">Time</p> */}
                                      <h6 class="text-sm mb-0">{time}</h6>
                                    </div>
                                  </td>
                                </tr>
                              </React.Fragment>
                            )
                          })}
       
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              
              </div>
            </div>
            </div>
          </main>
          
        )
    }
                               
  }

export default contentPage
